sourcesContent:
import { getConnection } from './../database/database';

const getLogins = async (req, res) => {
    try {
        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM tbl_login");
        res.json(result);   
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

const autenticacion = async (req, res) => {
    try {
        const { log_correo, log_password } = req.body;
        if (log_correo == undefined) {
            res.status(400);
            res.send("log_correo not defined");
        }
        if (log_password == undefined) {
            res.status(400);
            res.send("log_password not defined");
        }
        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM tbl_login WHERE log_correo = ? AND log_password = ? AND log_estado = 1", [log_correo, log_password]);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
}

const insertUsuario = async (req, res) => {
    try{
        const log_estado = 1;
        const { log_correo, log_password, log_name_imagen, log_imagen } = req.body;
        if (log_correo == undefined) {
            res.status(400);
            res.send("log_correo not defined");
        }
        if (log_password == undefined) {
            res.status(400);
            res.send("log_password not defined");
        }
        if (log_name_imagen == undefined) {
            res.status(400);
            res.send("log_name_imagen not defined");
        }
        if (log_imagen == undefined) {
            res.status(400);
            res.send("log_imagen not defined");
        }
        const login = {log_correo, log_password, log_name_imagen, log_imagen, log_estado};
        const connection = await getConnection();
        const result = await connection.query("INSERT INTO tbl_login SET ?", login);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
}

export const methods = {
    autenticacion,
    getLogins,
    insertUsuario,
};

